def generate_error_list(lst):
    new_list = []
    for num in lst:
        # 生成一个 -0.2 到 0.2 之间的随机误差
        error = random.uniform(-0.3, 0.35)
        # 计算新的数
        new_num = num * (1 + error)
        new_list.append(new_num)
    return new_list

